INSERT INTO `configuration` VALUES ('433', 'Categories-Tabs Menu ON/OFF', 'CATEGORIES_TABS_STATUS', '1', 'Categories-Tabs<br />This enables the display of your store\'s categories as a menu across the top of your header. There are many potential creative uses for this.<br />0= Hide Categories Tabs<br />1= Show Categories Tabs', '19', '112', null, '2014-06-18 23:20:53', null, 'zen_cfg_select_option(array(\'0\', \'1\'), ');
