INSERT INTO `configuration` VALUES ('435', 'Skip 1-prod Categories', 'SKIP_SINGLE_PRODUCT_CATEGORIES', 'True', 'Skip single-product categories<br />If this option is set to True, then if the customer clicks on a link to a category which only contains a single item, then Zen Cart will take them directly to that product-page, rather than present them with another link to click in order to see the product.<br />Default: True', '19', '120', null, '2014-06-18 23:20:53', null, 'zen_cfg_select_option(array(\'True\', \'False\'), ');
INSERT INTO `configuration` VALUES ('436', 'Use split-login page', 'USE_SPLIT_LOGIN_MODE', 'False', 'The login page can be displayed in two modes: Split or Vertical.<br />In Split mode, the create-account options are accessed by clicking a button to get to the create-account page.  In Vertical mode, the create-account input fields are all displayed inline, below the login field, making one less click for the customer to create their account.<br />Default: False', '19', '121', null, '2014-06-18 23:20:53', null, 'zen_cfg_select_option(array(\'True\', \'False\'), ');
