INSERT INTO `configuration` VALUES ('530', 'EZ-Pages Prev/Next Buttons', 'EZPAGES_SHOW_PREV_NEXT_BUTTONS', '2', 'Display Prev/Continue/Next buttons on EZ-Pages pages?<br />0=OFF (no buttons)<br />1=\"Continue\"<br />2=\"Prev/Continue/Next\"<br /><br />Default setting: 2.', '30', '30', null, '2014-06-18 23:20:53', null, 'zen_cfg_select_option(array(\'0\', \'1\', \'2\'), ');
